import React, { useEffect, useRef, useState } from 'react';

// Define the types of sections available
export type Section = 'home' | 'creator' | 'subscribe' | 'support' | 'contact';

// Define the props with the section type
interface HeaderProps {
  onSectionChange: (section: Section) => void;
  currentSection: Section;
}

const Header: React.FC<HeaderProps> = ({ onSectionChange, currentSection }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [underlineStyle, setUnderlineStyle] = useState({ left: 0, width: 0 });
  const homeRef = useRef<HTMLButtonElement>(null);
  const creatorRef = useRef<HTMLButtonElement>(null);
  const subscribeRef = useRef<HTMLButtonElement>(null);
  const supportRef = useRef<HTMLButtonElement>(null);
  const contactRef = useRef<HTMLAnchorElement>(null);

  // Mapping section names to their corresponding refs
  const sectionToRefMap: Record<Section, React.RefObject<HTMLButtonElement | HTMLAnchorElement>> = {
    home: homeRef,
    creator: creatorRef,
    subscribe: subscribeRef,
    support: supportRef,
    contact: contactRef,
  };

  useEffect(() => {
    const selectedRef = sectionToRefMap[currentSection].current;
    if (selectedRef) {
      const { offsetLeft, offsetWidth } = selectedRef;
      setUnderlineStyle({ left: offsetLeft, width: offsetWidth });
    }
  }, [currentSection]);

  // Toggle the menu state for mobile devices
  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  return (
    <div className="pt-10 px-6 sm:px-24 flex justify-between items-center">
      <div className="flex items-center text-white font-bold">
        <img src="/pulse-logo.svg" alt="Pulse Logo" className="h-12" />
      </div>
      {/* Mobile Hamburger Menu Icon */}
      <div className="sm:hidden">
        <button onClick={toggleMenu} className="focus:outline-none">
          <svg className="w-8 h-8 text-zinc-800" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            {isMenuOpen ? (
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            ) : (
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
            )}
          </svg>
        </button>
      </div>
      {/* Desktop and Mobile Menu */}
      <div className={`sm:flex sm:items-center sm:gap-10 ${isMenuOpen ? 'block' : 'hidden'} sm:block`}>
        <button ref={homeRef} className="text-zinc-800 text-base font-medium capitalize" onClick={() => { setIsMenuOpen(false); onSectionChange('home'); }}>Features</button>
        <button ref={creatorRef} className="text-gray-700 text-base font-medium capitalize" onClick={() => { setIsMenuOpen(false); onSectionChange('creator'); }}>Creators</button>
        <button ref={subscribeRef} className="text-gray-700 text-base font-medium capitalize" onClick={() => { setIsMenuOpen(false); onSectionChange('subscribe'); }}>Subscribe</button>
        {/* Uncomment if needed */}
        {/* <button ref={supportRef} className="text-gray-700 text-base font-medium capitalize" onClick={() => { setIsMenuOpen(false); onSectionChange('support'); }}>Support</button> */}
        <a ref={contactRef} href="mailto:pulsefitnessapp@gmail.com" className="text-gray-700 text-base font-medium capitalize" onClick={() => setIsMenuOpen(false)}>Contact Us</a>
      </div>
      {/* Underline */}
      <div className="hidden sm:block relative w-[570px] h-[39px]">
        <div className="absolute bottom-0 h-[1.80px] bg-black transition-all duration-300 ease-in-out" style={{ ...underlineStyle }}></div>
      </div>
    </div>
  );
};

export default Header;
