import React, { useState, useEffect } from 'react';
import SequentialVideoPlayerView from '../components/SequentialVideoPlayerView';
import SweatListCardView from '../components/SweatListCardView';
import WorkoutService from '../services/WorkoutService';
import { ExerciseLog } from '../types/ExerciseLog';
import { Workout } from '../types/Workout';

interface WorkoutPreviewerProps {
  userId: string;
  workoutId: string;
  onClose: () => void;
  onUseSweatlist: () => void;
}

const WorkoutPreviewer: React.FC<WorkoutPreviewerProps> = ({ userId, workoutId, onClose, onUseSweatlist }) => {
  const [workout, setWorkout] = useState<Workout | null>(null);
  const [logs, setLogs] = useState<ExerciseLog[]>([]);
  const [error, setError] = useState(false);

  useEffect(() => {
    const fetchWorkout = async () => {
      const [fetchedWorkout, fetchedLogs] = await WorkoutService.sharedInstance.fetchSavedWorkout(userId, workoutId);
      if (fetchedWorkout) {
        setWorkout(fetchedWorkout);
        setLogs(fetchedLogs || []);
      } else {
        setError(true);
      }
    };

    fetchWorkout();
  }, [userId, workoutId]);

  if (error || !workout) {
    return <div>Error loading workout</div>;
  }

  const videoURLs = logs.flatMap(log => log.exercise.videos.map(video => video.videoURL));

  return (
    <div className="relative h-screen bg-secondaryCharcoal">
      <SequentialVideoPlayerView videoURLs={videoURLs} isMuted={true} ratio="cover" />

      <div className="absolute inset-0 bg-secondaryCharcoal bg-opacity-50">
        <div className="flex flex-col h-full">
          <div className="flex justify-between p-4">
            <button onClick={onClose} className="text-secondaryWhite">Close</button>
          </div>

          <div className="flex-grow overflow-y-auto">
            <div className="text-center text-secondaryWhite mt-20">
              <h2 className="font-bold">Your workout for today:</h2>
              <p className="font-bold">{workout.determineWorkoutZone()}</p>
            </div>

            {/* Workout stats */}
            <div className="flex justify-around mt-8 mb-4">
              <div className="text-center">
                <p className="text-xl font-bold text-primaryGreen">{logs.length}</p>
                <p className="text-sm text-secondaryWhite/70">Exercises</p>
              </div>
              <div className="text-center">
                <p className="text-xl font-bold text-primaryGreen">{workout.estimatedDuration()}</p>
                <p className="text-sm text-secondaryWhite/70">Duration</p>
              </div>
            </div>

            {/* Exercise list */}
            <div className="mt-4">
              {logs.map((log, index) => (
                <SweatListCardView
                  key={log.id}
                  log={log}
                />
              ))}
            </div>
          </div>

          <div className="p-4 bg-secondaryBlack">
            <button 
              onClick={onUseSweatlist}
              className="w-full py-3 bg-primaryGreen text-secondaryBlack font-bold rounded-lg"
            >
              Use Sweatlist
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WorkoutPreviewer;
