import React, { useState } from 'react';

interface Props {
  isOpen: boolean;
  closePopup: () => void;
}

const EmailPopup: React.FC<Props> = ({ isOpen, closePopup }) => {
  const [email, setEmail] = useState<string>('');

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    try {
      const response = await fetch('/.netlify/functions/addEmail', {
        method: 'POST',
        body: JSON.stringify({ email }),
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      console.log('Submission success:', data);
      closePopup();
    } catch (error) {
      console.error('Error submitting email:', error);
    }
  };

  if (!isOpen) return null;

  return (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
      <form onSubmit={handleSubmit} style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', background: 'white', padding: '20px', borderRadius: '8px' }}>
        <h2>Subscribe to Updates</h2>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          required
        />
        <button type="submit">Submit</button>
        <button type="button" onClick={closePopup}>Cancel</button>
      </form>
    </div>
  );
};

export default EmailPopup;
